/*Table structure for table `sch_teacher` */

DROP TABLE IF EXISTS `sch_teacher`;

CREATE TABLE `sch_teacher` (
  `teacher_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '教师ID',
  `mobile` varchar(50) NOT NULL DEFAULT '0' COMMENT '教师联系方式',
  `name` varchar(50) NOT NULL COMMENT '教师名称',
  `subject` tinyint(4) NOT NULL DEFAULT '0' COMMENT '所教学科，1为语文，2为数学，3为英语，4为计算机，5为音乐（后面扩展），0为没有学科',
  `address` varchar(100) NOT NULL DEFAULT '' COMMENT '教师居住地',
  `department` tinyint(4) NOT NULL DEFAULT '0' COMMENT '所属部门，1为教师队伍，没有所属部门，2为教务处成员，3办公室成员，4为评审委员会成员,5为校长',
  `is_admin` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是不是部门负责人，1为负责人（主任等），0为普通职员',
  `point` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '现阶段的积点',
  `level` int(11) NOT NULL DEFAULT '0' COMMENT '教师现在所处的等级',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改的时间',
  `sex` tinyint(4) NOT NULL DEFAULT '0' COMMENT '教师性别,1男，2女',
  `teacher_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否在职，1为在职',
  `born` date DEFAULT NULL COMMENT '出生日期',
  `education` tinyint(4) DEFAULT '0' COMMENT '学历，1大学本科，2研究生，3硕士研究生',
  `now_level_info` varchar(100) DEFAULT '' COMMENT '现聘岗位',
  `now_level` varchar(50) DEFAULT NULL COMMENT '岗位级别',
  `work_start_time` date DEFAULT NULL COMMENT '现聘岗位起始时间',
  `now_work_duty` varchar(100) DEFAULT NULL COMMENT '现任职务名称',
  `now_work_time` varchar(250) DEFAULT NULL COMMENT '现任职务层次起始时间',
  `now_work_level` varchar(100) DEFAULT NULL COMMENT '现任职务层次',
  `work_time` varchar(250) DEFAULT NULL COMMENT '参加工作时间',
  `work_year` int(11) DEFAULT NULL COMMENT '工作年限',
  `school_work_time` varchar(250) DEFAULT NULL COMMENT '来校时间',
  `school_work_year` int(11) DEFAULT NULL COMMENT '来校年限',
  `er_school_time` varchar(250) DEFAULT NULL COMMENT '二外入编时间',
  `er_school_year` int(11) DEFAULT NULL COMMENT '二外入编年限',
  `qua_time` varchar(250) DEFAULT NULL COMMENT '职称取得时间',
  `qua_year` int(11) DEFAULT NULL COMMENT '职称年限',
  `qua_name` varchar(100) DEFAULT NULL COMMENT '职称',
