/*Table structure for table `sch_teaching_performance` */

DROP TABLE IF EXISTS `sch_teaching_performance`;

CREATE TABLE `sch_teaching_performance` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `alias` varchar(100) NOT NULL DEFAULT '' COMMENT '别名，用于处理表提交的时候ID的问题',
  `topic` varchar(500) NOT NULL DEFAULT '' COMMENT '绩效考核的题目',
  `type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '1．教学绩效类,2．专业发展类,3教育与管理类',
  `point` int(11) NOT NULL DEFAULT '0' COMMENT '题目分值',
  `create_time` timestamp NULL DEFAULT '0000-00-00 00:00:00',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;

/*Data for the table `sch_teaching_performance` */

insert  into `sch_teaching_performance`(`id`,`alias`,`topic`,`type`,`point`,`create_time`,`update_time`) values (1,'work_load','以学期为单位，聘任期内每一阶段都承担满量教学任务',1,10,'2016-09-22 09:33:16','2016-10-23 09:04:59'),(2,'teaching_good','教学效果良好（段考教学质量评估、排名、进步率参照B值、均分、学科竞赛、高考等等）',1,10,'2016-09-22 09:33:28','2016-10-23 09:05:10'),(3,'teaching_behavior','教学行为规范，无违反学校教学常规要求行为（即教学事故，如无故缺课或缺监考、上课或监考迟到、打骂学生、课堂秩序混乱、试题泄露、不执行或未完成教学计划、有偿家教等等）',1,10,'2016-09-22 09:33:41','2016-10-23 09:05:21'),(4,'student_statis','积极承担学校八大学堂、校本选修课、社会实践课等课程的开发与实施，教学效果好，学生满意度80%以上',1,10,'2016-09-22 09:33:58','2016-10-23 09:05:44'),(5,'subject_good','积极参与各项科组建设活动（科组、备课组例会参会情况、示范课、课改课、研究课、竞赛课、特色科组创建等等）',1,10,'2016-09-22 09:34:14','2016-10-23 09:05:56'),(6,'academic','主持或参与课题或项目研究并取得相应的研究成果。',2,2,'2016-09-22 09:35:50','2016-10-23 09:06:04'),(7,'organ_sub','组织或参与学校课改、科研项目并取得相应的成果。',2,2,'2016-09-22 09:36:00','2016-10-23 09:06:25'),(8,'school_forum','在校内外各类教育教学论坛、会议、培训会上做过的学术讲座或报告、发表过文章。',2,2,'2016-09-22 09:36:12','2016-10-23 09:06:48'),(9,'backtone_teacher','参加国家、省、市各级骨干教师培训并学有所成，是否市级以上骨干教师',2,2,'2016-09-22 09:36:22','2016-10-23 09:07:16'),(10,'teach_intern','参与学校的青年教师培养计划并高质量完成任务，是否师徒结对、是否带实习生。',2,1,'2016-09-22 09:36:31','2016-10-23 09:07:39'),(11,'person_write','其他专业发展成果，是否出版发行个人专著。',2,1,'2016-09-22 09:36:45','2016-10-23 09:07:49'),(12,'semester','以学期为单位，每一阶段都承担教育教学管理类工作',3,5,'2016-09-22 09:44:10','2016-10-23 09:07:58'),(13,'head_teacher','班主任工作教育效果良好，按时保质保量完成各项常规管理工作，获学期“文明班级”综合荣誉',3,5,'2016-09-22 09:44:20','2016-10-23 09:08:14'),(14,'learning_exper','注重班主任专业发展，积极参与各级各类名班主任培训活动，及时上交班主任计划、总结、学习心得、经验总结等。',3,4,'2016-09-22 09:44:34','2016-10-23 09:08:42'),(15,'class_meeting','积极承担学校各类德育、活动课程的开发与实施（主持或协助开展班主任沙龙、专业发展论坛，主动进行班会课展示或经验介绍，指导各类学生社团和学生活动、打造优秀社团或优质学生活动等）',3,3,'2016-09-22 09:44:54','2016-10-23 09:08:59'),(16,'good_head_teacher','获得各级各类德育奖项（优秀班主任、优秀教育工作者及发表德育文章或出版德育专著等）。省级5分，市级3分，校级2分',3,5,'2016-09-22 09:45:11','2016-10-23 09:09:07'),(17,'fes_activity','积极参与学校艺术节、体育节、外语节、社团节等各类大型活动的组织、管理与服务工作，在活动中承担某个项目的具体工作。',3,3,'2016-09-22 09:45:29','2016-10-23 09:09:37'),(18,'work_for_school','以主人翁意识参与学校工作，传播正能量，为完善学校各项工作，主动以书面形式向学校及各部门提出建设性意见并被部门采纳的',3,5,'2016-09-22 09:45:40','2016-10-23 09:09:46'),(19,'manange_school','因工作需要，服从学校安排，从事其他教育管理岗位且工作突出，或配合学校各部门及时处理各种突发危机事件，关键时刻能顶岗',3,5,'2016-09-22 09:45:51','2016-10-23 09:09:56'),(20,'research_school','善于进行教育管理实践及科研，在班级管理、课堂教学等方面有扎实、创新、成熟、有效的做法且被全年级或全校推广开展',3,5,'2016-09-22 09:46:01','2016-10-23 09:10:20');

